import React, {useState} from 'react';
import valid from "./valid";

const Login = () => {
    const [values, setvalues] = useState({
        fullname: "",
        email: "",
        password: "", 
    });
    const handleChange = (event) => {
    setvalues({
        ...values,
        [event.target.name]: event.target.value,
    });
};

const [errors, setErrors] = useState({});

    const handleFormSubmit = (event) => {
        event.preventDefault();
        setErrors(valid(values))
    };
   
    return(
        <div className="container" >
        <div className="app-wrapper"> 
        <div>
        <h1 className="title">LOGIN PAGE</h1>
    </div>
    
    <form className="form-wrapper">
   <div className="name">
       <lable className="label">Full Name</lable>
       <input className="input" type="text" name="fullname" value={values.fullname} onChange={handleChange} />
       {errors.fullname && <p className="errors">{errors.fullname}</p>}
   </div>

   <div className="email">
       <lable className="label">Email</lable>
       <input className="input" type="email" name="email" value={values.email} onChange={handleChange} />
       {errors.email && <p className="errors">{errors.email}</p>}
</div>

   <div className="password">
       <lable className="label">Password</lable>
       <input className="input" type="password" name="password" value={values.password} onChange={handleChange} />
       {errors.password && <p className="errors">{errors.password}</p>}
       </div>
   <div>
       <button className="submit" onClick={handleFormSubmit}>
           Login
       </button>

   </div>
   </form>
   </div>
   </div>
    )
}


    
    

export default Login;
